import { Steps } from "antd";
import { useState } from "react";
import ContactDetailsForm from "../components/ContactDetailsFrom";
import swal from "sweetalert";
import PersonalDetailsForm from "../components/PersonalDetailsForm";

const PersonalDetails = () => {
  const [current, setCurrent] = useState(0);
  const [personalDetails, setPersonalDetails] = useState(null);
  const [contactDetails, setContactDetails] = useState(null);
  const [formDetails, setFormDetails] = useState(null);

  const onFinishPersonDetails = (values) => {
    console.log("onFinishPersonDetails", values);
    setPersonalDetails(values);
    setCurrent(1);
  };
  const onFinishContactDetails = async (values) => {
    setContactDetails(values);
    const combinedFormData = { ...personalDetails, ...values };
    setFormDetails(combinedFormData);
    setCurrent(2);
    let result = await fetch("http://localhost:8000/api/personalDetails",{
      method: 'POST',
      headers:{
        "Content-Type" :"application/json",
        "Accept" : "application/json"
      },
      body:JSON.stringify(combinedFormData)
    });
    result = await result.json();
    console.warn("result",result);
    localStorage.setItem("user-info",JSON.stringify(result));
  };
  const forms = [
    <PersonalDetailsForm
      onFinish={onFinishPersonDetails}
      initialValues={personalDetails}
    />,
    <ContactDetailsForm
      onFinish={onFinishContactDetails}
      initialValues={contactDetails}
    />,
    // <FinishForm />,
  ];
  const isStepDisabled = (stepNumber) => {
    if (stepNumber === 0) {
      return false;
    }
    if (stepNumber === 1) {
      return personalDetails === null;
    }
    if (stepNumber === 2) {
      return personalDetails === null || contactDetails === null;
    }
  };
  return (
    <div>
      <Steps
        style={{ padding: "32px 16px" }}
        onChange={setCurrent}
        current={current}
      >
        <Steps.Step disabled={isStepDisabled(0)} title="Personal Details" />
        <Steps.Step disabled={isStepDisabled(1)} title="Contact Details" />
        {/* <Steps.Step disabled={isStepDisabled(2)} title="Finish" /> */}
      </Steps>
      {forms[current]}
    </div>
  );
};
export default PersonalDetails;
